class MobileMenu{constructor(){this.siteHeader=$(".site-header"),this.menuIcon=$(".site-header__menu-icon"),this.menuContent=$(".site-header__menu-content"),this.events()}events(){this.menuIcon.click(()=>this.toggleTheMenu())}toggleTheMenu(){this.menuContent.toggleClass("site-header__menu-content--is-visible"),this.siteHeader.toggleClass("site-header--is-expanded"),this.menuIcon.toggleClass("site-header__menu-icon--close-x")}}const test=new MobileMenu;class RevealOnScroll{constructor(e,t){this.itemsToReveal=e,this.animationDelay=t,this.hideInitially(),this.createOnScroll()}hideInitially(){this.itemsToReveal.addClass("reveal-item")}createOnScroll(){const e=this;[...this.itemsToReveal].forEach(t=>{document.addEventListener("scroll",function(){const s=window.scrollY,i=t.offsetTop,o=t.offsetHeight;s>=i-o/e.animationDelay?t.classList.add("reveal-item--is-visible"):s<i-o/e.animationDelay&&t.classList.remove("reveal-item--is-visible")})})}}new RevealOnScroll($(".feature-item"),.5),new RevealOnScroll($(".testimonial"),1);class StickyHeader{constructor(){this.siteHeader=$(".site-header"),this.siteHeaderChangeColor(),this.pageSections=$(".page-section"),this.headerLinks=$("nav a"),this.scrollToSection()}siteHeaderChangeColor(){const e=this;$(document).scroll(function(){$(document).scrollTop()>1.5*$(e.siteHeader).outerHeight()?$(e.siteHeader).addClass("site-header--sticky"):$(e.siteHeader).removeClass("site-header--sticky")})}scrollToSection(){const e=this;this.pageSections.each(function(){const t=this,s=t.getAttribute("data-matching-link");$(s).click(function(){$(e.headerLinks).each(function(){$(this).removeClass("orange-highlight")}),$(s).addClass("orange-highlight"),$("html, body").animate({scrollTop:$(t).offset().top},500)})})}}new StickyHeader;class OrangeButtonAtScroll{constructor(){this.pageSections=$(".page-section"),this.headerLinks=$("nav a"),this.addOrangeColorToButton()}addOrangeColorToButton(){const e=this;$(window).scroll(function(){$(e.headerLinks).each(function(){$(this).removeClass("orange-highlight")});const t=$(window).scrollTop();$(e.pageSections).each(function(){const e=$(this).offset().top,s=e+$(this).outerHeight();if(t>=Math.floor(e)&&t<Math.floor(s)){const e=this.getAttribute("data-matching-link");$(e).addClass("orange-highlight")}})})}}new OrangeButtonAtScroll;class Modal{constructor(){this.openModalButton=$(".open-modal"),this.closeModalButton=$(".modal--close"),this.modalToReveal=$(".modal"),this.events()}events(){$(this.openModalButton).click(this.showModal.bind(this)),$(this.closeModalButton).click(()=>this.hideModal()),$(window).keyup(this.hideModalOnEsc.bind(this))}showModal(){return $(this.modalToReveal).addClass("modal--is-visible"),!1}hideModal(){$(this.modalToReveal).removeClass("modal--is-visible")}hideModalOnEsc(e){console.log(e.keyCode),27===e.which&&$(this.modalToReveal).removeClass("modal--is-visible")}}new Modal;